import { useState, useEffect } from 'react';
import xml2js from 'xml2js';
import SearchBar from './searchBar';
import Sitemap from './sitemap';

import { Component } from 'react';

class App extends Component {
  state = {
    urls: [],
    filteredUrls: [],
    searchTerm: ""
  }


  componentDidMount() {
    fetch("https://www.smu.edu.sg/sitemap.xml")
    .then(res => res.text())
    .then((data) => {
      var parser = new xml2js.Parser();
      var jsondata = '';

      parser.parseString(data, function (err, result) {
          jsondata = result;
      }); 
      this.setState({ urls: jsondata.urlset.url });
    });

    this.setState({ filteredUrls: this.state.urls.filter(
      url => {
        return (
          url.loc.includes("student")
        );
      }
    )});

  }  

  render () {
    return (
      <>
      <SearchBar searchTerm={this.state.searchTerm} onSearch={mySearch => this.setState({ searchTerm })} />
      <Sitemap urls={this.statefilteredUrls} />
      </>
    );
  }  

}

export default App;
